import React from "react";
import Map from "./Map";
import "./Dashboard.css";

const Dashboard = ({
	locations,
	userLocation,
	setSelectedLocation,
	clickedIcons,
	setClickedIcons,
}) => {
	// Toggle the clicked state of a marker
	const handleIconClick = (key) => {
		setClickedIcons((prevState) => {
			const newState = { ...prevState, [key]: !prevState[key] }; // Toggle the clicked state
			return newState;
		});
	};

	return (
		<div className="dashboard">
			{/* Header */}
			<div className="dashboard-header">
				<img
					src="path-to-logo.png"
					alt="Logo"
					className="logo"
					onClick={() => window.open("https://komichinonoel.info/", "_blank")}
				/>
				<p>お気に入りのキャンドルに投票しよう</p>
				<button
					onClick={() =>
						window.open("https://forms.gle/uEqzwB1fGVb8FgsS8", "_blank")
					}
				>
					小径のノエル賞
				</button>
			</div>

			{/* Sidebar for Toggle Buttons */}
			<div className="toggle-sidebar">
				{locations.map((location) => (
					<button
						key={location.key}
						onClick={() => handleIconClick(location.key)}
					>
						Toggle {location.name}
					</button>
				))}
			</div>

			{/* Map */}
			<div className="map-container">
				<Map
					clickedIcons={clickedIcons}
					setSelectedLocation={setSelectedLocation}
					getLocation={() => {}}
					userLocation={userLocation}
					locations={locations}
				/>
			</div>

			{/* Footer */}
			<div className="dashboard-footer">キャンドルをクリックしてね！</div>
		</div>
	);
};

export default Dashboard;
