import React, { useState, useEffect } from "react";
import { Dashboard } from "./components";
import data from "./data.json";

function App() {
	// State management
	const [locations, setLocations] = useState(data);
	const [userLocation, setUserLocation] = useState();
	const [selectedLocation, setSelectedLocation] = useState();
	const [clickedIcons, setClickedIcons] = useState(() => {
		// Load persisted clicked icons state from localStorage
		const savedState = localStorage.getItem("clickedIcons");
		return savedState ? JSON.parse(savedState) : {};
	});

	// Persist clicked icons state in localStorage
	useEffect(() => {
		if (clickedIcons) {
			localStorage.setItem("clickedIcons", JSON.stringify(clickedIcons));
		}
	}, [clickedIcons]);

	return (
		<div className="app">
			<Dashboard
				locations={locations}
				setLocations={setLocations}
				userLocation={userLocation}
				setUserLocation={setUserLocation}
				selectedLocation={selectedLocation}
				setSelectedLocation={setSelectedLocation}
				clickedIcons={clickedIcons}
				setClickedIcons={setClickedIcons}
			/>
		</div>
	);
}

export default App;
