import React, { useState, useEffect } from "react";
import "./Dashboard.css";
import Map from "./Map";
import logo from "../noel-logo.png";
import iconDefault from "../candle-icon-default.png"; // Default icon
import iconClicked from "../candle-icon-clicked.png"; // New icon after clicked

function Dashboard(props) {
	const [clickedIcons, setClickedIcons] = useState({});

	// Initialize clickedIcons state from localStorage on component mount
	useEffect(() => {
		const savedClickedIcons = localStorage.getItem("clickedIcons");
		if (savedClickedIcons) {
			setClickedIcons(JSON.parse(savedClickedIcons)); // Parse and set the saved state
		}
	}, []);

	// Update localStorage whenever clickedIcons state changes
	useEffect(() => {
		if (Object.keys(clickedIcons).length > 0) {
			localStorage.setItem("clickedIcons", JSON.stringify(clickedIcons));
		}
	}, [clickedIcons]);

	const getLocation = () => {
		if (navigator.geolocation) {
			navigator.geolocation.getCurrentPosition(getCoordinates);
		}
	};

	const getCoordinates = (position) => {
		props.setUserLocation({
			latitude: position.coords.latitude,
			longitude: position.coords.longitude,
		});
	};

	// Handle the dashboard button click and update the state
	const handleDashboardClick = (iconId) => {
		setClickedIcons((prev) => ({
			...prev,
			[iconId]: !prev[iconId], // Toggle the icon state
		}));
	};

	return (
		<div className="screen">
			<div className="dashboard__header">
				<img
					alt="Logo"
					className="logo"
					src={logo}
					onClick={() => window.open("https://komichinonoel.info/", "_blank")}
				/>
				<text className="header__button__label">
					お気に入りの <br />
					キャンドルに投票しよう
				</text>
				<button
					className="header__button"
					onClick={() =>
						window.open("https://forms.gle/uEqzwB1fGVb8FgsS8", "_blank")
					}
				>
					小径のノエル賞
				</button>
			</div>

			<Map
				id="map"
				getLocation={getLocation}
				locations={props.locations}
				userLocation={props.userLocation}
				selectedLocation={props.selectedLocation}
				setSelectedLocation={props.setSelectedLocation}
			/>

			{props.selectedLocation ? (
				<div className="dashboard__container">
					<div className="venue">
						<div className="venue__name">{props.selectedLocation.title}</div>
						<div className="venue__type">{props.selectedLocation.type}</div>
					</div>

					<button
						className="dashboard__btn"
						onClick={(e) => {
							e.preventDefault();
							window.location.href = `${props.selectedLocation.link}`;
						}}
					>
						Google Maps
					</button>

					{/* Icons section */}
					<div className="icons-container">
						<img
							src={
								clickedIcons[props.selectedLocation.id]
									? iconClicked
									: iconDefault
							}
							alt="Candle Icon"
							onClick={() => handleDashboardClick(props.selectedLocation.id)}
							className="icon"
						/>
					</div>
				</div>
			) : (
				<p className="dashboard__container">
					キャンドルを
					<br />
					クリックしてね！
				</p>
			)}
		</div>
	);
}

export default Dashboard;
