import React, { useState } from "react";
import Map from "./Map"; // Adjust the path if needed

const Dashboard = ({
	locations,
	setLocations,
	userLocation,
	setUserLocation,
	selectedLocation,
	setSelectedLocation,
	clickedIcons,
	setClickedIcons,
}) => {
	// Toggle the clicked state of a marker
	const handleIconClick = (key) => {
		setClickedIcons((prevState) => {
			const newState = { ...prevState, [key]: !prevState[key] }; // Toggle the clicked state
			return newState;
		});
	};

	return (
		<div className="dashboard">
			<h1>Dashboard</h1>

			{/* Example buttons for toggling clicked icons */}
			{locations.map((location) => (
				<button
					key={location.key}
					onClick={() => handleIconClick(location.key)}
				>
					Toggle {location.name}
				</button>
			))}

			{/* Pass down clickedIcons state to the Map component */}
			<Map
				clickedIcons={clickedIcons}
				setSelectedLocation={setSelectedLocation}
				getLocation={() => {}}
				userLocation={userLocation}
				locations={locations}
			/>
		</div>
	);
};

export default Dashboard;
