import React, { useEffect } from "react";
import { withGoogleMap, GoogleMap, Marker } from "react-google-maps";
import candleIcon from "../candle-icon.png";
import visitedCandleIcon from "../visited-candle-icon.png";
import mapStyle from "./mapStyle.json";

// Marker icons
const defaultIcon = new window.google.maps.MarkerImage(
	candleIcon,
	null,
	null,
	null,
	new window.google.maps.Size(17, 32)
);

const visitedIcon = new window.google.maps.MarkerImage(
	visitedCandleIcon,
	null,
	null,
	null,
	new window.google.maps.Size(17, 32)
);

const MyMap = withGoogleMap((props) => (
	<GoogleMap
		options={{
			styles: mapStyle,
			gestureHandling: "greedy",
			zoomControl: false,
			mapTypeControl: false,
			scaleControl: false,
			streetViewControl: false,
			rotateControl: false,
			fullscreenControl: false,
		}}
		ref={props.onMapLoad}
		defaultZoom={16}
		defaultCenter={{ lat: 35.6607, lng: 139.6682804608054 }}
	>
		{props.locations.map((location) => (
			<Marker
				key={location.key}
				position={location.position}
				title={location.name}
				icon={
					props.visitedLocations.includes(location.key)
						? visitedIcon
						: defaultIcon
				}
				onClick={() =>
					props.setSelectedLocation({
						key: location.key,
						position: location.position,
						title: location.name,
						type: location.type,
						link: location.link,
					})
				}
			/>
		))}
	</GoogleMap>
));

export default function Map(props) {
	useEffect(() => {
		props.getLocation();
	}, [props]);

	return (
		<MyMap
			className="map"
			containerElement={<div style={{ height: `85vh` }} />}
			mapElement={<div style={{ height: `85vh` }} />}
			onMapLoad={() => {}}
			markers={[]}
			onMarkerRightClick={() => {}}
			setSelectedLocation={props.setSelectedLocation}
		/>
	);
}
