import React, { useEffect, useMemo } from "react";
import { GoogleMap, Marker, useLoadScript } from "@react-google-maps/api";
import candleIcon from "../candle-icon.png";
import visitedCandleIcon from "../visited-candle-icon.png";
import noelCandle1 from "../noelCandle-1.png";
import noelCandle2 from "../noelCandle-2.png";
// ... import all noelCandle icons
import mapStyle from "./mapStyle.json";

// Unique icons for each location
const candleIcons = {
	1: noelCandle1,
	2: noelCandle2,
	// ... add other mappings
};

const createMarkerIcon = (key, isVisited) => {
	return isVisited
		? candleIcons[key] || visitedCandleIcon
		: candleIcons[key] || candleIcon;
};

const mapContainerStyle = {
	height: "85vh",
	width: "100%",
};

const center = { lat: 35.6607, lng: 139.6682804608054 };

const libraries = ["places"]; // Declare libraries as a constant outside the component

export default function Map(props) {
	const { isLoaded } = useLoadScript({
		googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
		libraries, // Use the static variable here
	});

	const markers = useMemo(() => {
		return props.locations?.map((location) => ({
			key: location.key,
			position: location.position,
			title: location.name,
			icon: createMarkerIcon(
				location.key,
				props.visitedLocations.includes(location.key)
			),
		}));
	}, [props.locations, props.visitedLocations]);

	useEffect(() => {
		props.getLocation();
	}, [props]);

	if (!isLoaded) return <div>Loading...</div>;

	return (
		<GoogleMap
			mapContainerStyle={mapContainerStyle}
			zoom={16}
			center={center}
			options={{
				styles: mapStyle,
				gestureHandling: "greedy",
				disableDefaultUI: true,
			}}
		>
			{markers?.map((marker) => (
				<Marker
					key={marker.key}
					position={marker.position}
					title={marker.title}
					icon={marker.icon}
					onClick={() =>
						props.setSelectedLocation({
							key: marker.key,
							position: marker.position,
							title: marker.title,
							link: marker.link,
						})
					}
				/>
			))}
		</GoogleMap>
	);
}
